DROP TABLE IF EXISTS `ast_Employee_T`;

CREATE TABLE `ast_Employee_T` ( `empId` VARCHAR(256) NOT NULL, `contactId` VARCHAR(64) NULL DEFAULT NULL, `dept` VARCHAR(256) NOT NULL, `designation` VARCHAR(256) NOT NULL, `jobType` VARCHAR(256) NOT NULL, `pan` VARCHAR(256) NOT NULL, `createdBy` VARCHAR(64) NULL DEFAULT '-1', `createdDate` DATETIME NULL DEFAULT '1900-01-01', `updatedBy` VARCHAR(64) NULL DEFAULT '-1', `updatedDate` DATETIME NULL DEFAULT '1900-01-01', `versionId` INT(11) NULL DEFAULT '-1', `activeStatus` INT(1) NULL DEFAULT '1', `txnAccessCode` INT(11) NULL DEFAULT NULL, PRIMARY KEY (`empId`));

